mapper.select:
		select 

	     from ${p.table_name} where 1=1

	    <#list p.gci_columns?sort_by("ordinal_position") as u>
	    <#if u.column_javatype=="String">

     	 	and ${u.clumn_name} like CONCAT('%',#${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"},'%')

   	<#elseif u.column_javatype=="Long">

     	 	and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}

   	<#elseif u.column_javatype=="int">

     	 	and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}

   	 <#elseif u.column_javatype=="long">

     	 	and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}

	    <#else>

     	 	and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}

   	 </#if>
    	</#list>


			limit 0, #${"{pageSize"},jdbcType=INTEGER}


			limit ${"$"}${"{(currentPage-1)*pageSize"}},${"$"}${"{pageSize"}}

select count(${p.pk_name}) as totalCount,#${"{pageSize"},jdbcType=INTEGER} as pageSize,#${"{currentPage"},jdbcType=INTEGER} as currentPage from ${p.table_name} where 1=1
<#list p.gci_columns?sort_by("ordinal_position") as u> <#if u.column_javatype=="String">
and ${u.clumn_name} like CONCAT('%',#${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"},'%')
<#elseif u.column_javatype=="Long">
and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}
<#elseif u.column_javatype=="int">
and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}
<#elseif u.column_javatype=="long">
and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}
<#else>
and ${u.clumn_name} = #${"{vo."+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}
</#if> </#list>

mapper.update:
update ${p.table_name}
<#list p.gci_columns?sort_by("ordinal_position") as u> <#if u.clumn_name=="id"><#else>
${u.clumn_name} = #${"{"+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"},
</#if> </#list>
where ${p.pk_name} = #${"{id"},jdbcType=${p.pk_obj_type?upper_case}}

update ${p.table_name} set <#list p.gci_columns?sort_by("ordinal_position") as u> <#if u.clumn_name=="id"><#else>
<#if !u_has_next>${u.clumn_name} = #${"{"+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"}<#else>${u.clumn_name} = #${"{"+u.clumn_name},jdbcType=${u.column_javaMybatintype?upper_case+"}"},</#if>
</#if> </#list> where ${p.pk_name} = #${"{id"},jdbcType=${p.pk_obj_type?upper_case}}
